     1→import { createClient, type User } from '@supabase/supabase-js';
     2→import { getCookie } from 'hono/cookie';
     3→import { createMiddleware } from 'hono/factory';
     4→import { HTTPException } from 'hono/http-exception';
     5→import { env } from '@shared/environment';
     6→
     7→const supabase = createClient(env.supabase.url, env.supabase.anonKey);
     8→
     9→/**
    10→ * Middleware to check if the user is authenticated.
    11→ * It checks for a Bearer token in the Authorization header or a token cookie.
    12→ * If the token is valid, it sets the user and token in the context.
    13→ * If the token is invalid or expired, it throws a 401 error.
    14→ * If the token is missing, it throws a 401 error.
    15→ * If there is an error with the authentication service, it throws a 500 error.
    16→ */
    17→export const authenticated = createMiddleware<{
    18→  Variables: {
    19→    user: User;
    20→    token: string;
    21→  };
    22→}>(async (c, next) => {
    23→  let token: string | undefined;
    24→
    25→  const authorizationHeader = c.req.header('Authorization');
    26→
    27→  if (authorizationHeader?.startsWith('Bearer ')) {
    28→    token = authorizationHeader.slice(7);
    29→  } else {
    30→    token = getCookie(c, 'token');
    31→  }
    32→
    33→  console.log('Token:', token);
    34→
    35→  if (!token) {
    36→    throw new HTTPException(401, {
