import React, { useState, useEffect } from 'react';
import { ShortAnswerQuestion, QuestionComponentProps } from '../types';
import QuestionWrapper from '../core/QuestionWrapper';

export const ShortAnswer: React.FC<QuestionComponentProps<string>> = ({
  question,
  value = '',
  onChange,
  onValidate,
  disabled = false,
  readOnly = false,
  error,
  className = ''
}) => {
  const q = question as ShortAnswerQuestion;
  const [localValue, setLocalValue] = useState(value);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [filteredSuggestions, setFilteredSuggestions] = useState<string[]>([]);

  useEffect(() => {
    setLocalValue(value);
  }, [value]);

  useEffect(() => {
    if (q.suggestions && localValue && !disabled && !readOnly) {
      const filtered = q.suggestions.filter(s => 
        s.toLowerCase().includes(localValue.toLowerCase())
      );
      setFilteredSuggestions(filtered);
      setShowSuggestions(filtered.length > 0);
    } else {
      setShowSuggestions(false);
    }
  }, [localValue, q.suggestions, disabled, readOnly]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const newValue = e.target.value;
    if (q.maxLength && newValue.length > q.maxLength) return;

    setLocalValue(newValue);
    onChange(newValue);
  };

  const handleSuggestionClick = (suggestion: string) => {
    setLocalValue(suggestion);
    onChange(suggestion);
    setShowSuggestions(false);
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Escape') {
      setShowSuggestions(false);
    }
  };

  return (
    <QuestionWrapper
      question={question}
      value={localValue}
      onChange={onChange}
      onValidate={onValidate}
      disabled={disabled}
      readOnly={readOnly}
      error={error}
      className={className}
    >
      <div className="relative">
        <input
          type="text"
          value={localValue}
          onChange={handleChange}
          onKeyDown={handleKeyDown}
          onFocus={() => q.suggestions && setShowSuggestions(true)}
          onBlur={() => setTimeout(() => setShowSuggestions(false), 200)}
          placeholder={q.placeholder}
          disabled={disabled}
          readOnly={readOnly}
          pattern={q.pattern}
          maxLength={q.maxLength}
          className={`
            w-full px-3 py-2 border rounded-md
            ${disabled || readOnly ? 'bg-gray-100' : 'bg-white'}
            ${error ? 'border-red-500' : 'border-gray-300'}
            focus:outline-none focus:ring-2 focus:ring-blue-500
          `}
          aria-label={q.text}
          aria-required={q.required}
          aria-invalid={!!error}
        />

        {q.maxLength && (
          <div className="absolute right-2 top-2 text-xs text-gray-500">
            {localValue.length}/{q.maxLength}
          </div>
        )}

        {showSuggestions && filteredSuggestions.length > 0 && (
          <div className="absolute z-10 w-full mt-1 bg-white border border-gray-300 rounded-md shadow-lg max-h-60 overflow-y-auto">
            {filteredSuggestions.map((suggestion, index) => (
              <button
                key={index}
                type="button"
                onClick={() => handleSuggestionClick(suggestion)}
                className="w-full px-3 py-2 text-left hover:bg-blue-50 focus:bg-blue-50 focus:outline-none"
              >
                {suggestion}
              </button>
            ))}
          </div>
        )}
      </div>
    </QuestionWrapper>
  );
};

export default ShortAnswer;

