import React, { useState, useEffect, useRef } from 'react';
import { LongFormQuestion, QuestionComponentProps } from '../types';
import QuestionWrapper from '../core/QuestionWrapper';

export const LongForm: React.FC<QuestionComponentProps<string>> = ({
  question,
  value = '',
  onChange,
  onValidate,
  disabled = false,
  readOnly = false,
  error,
  className = ''
}) => {
  const q = question as LongFormQuestion;
  const [localValue, setLocalValue] = useState(value);
  const [wordCount, setWordCount] = useState(0);
  const textAreaRef = useRef<HTMLTextAreaElement>(null);

  useEffect(() => {
    setLocalValue(value);
    updateWordCount(value);
  }, [value]);

  useEffect(() => {
    adjustTextAreaHeight();
  }, [localValue]);

  const updateWordCount = (text: string) => {
    const words = text.trim().split(/\s+/).filter(word => word.length > 0);
    setWordCount(words.length);
  };

  const adjustTextAreaHeight = () => {
    if (textAreaRef.current) {
      textAreaRef.current.style.height = 'auto';
      textAreaRef.current.style.height = `${textAreaRef.current.scrollHeight}px`;
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    const newValue = e.target.value;

    if (q.maxLength && newValue.length > q.maxLength) return;

    setLocalValue(newValue);
    updateWordCount(newValue);
    onChange(newValue);
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLTextAreaElement>) => {
    if (q.enableMarkdown) {
      if (e.key === 'Tab') {
        e.preventDefault();
        const start = e.currentTarget.selectionStart;
        const end = e.currentTarget.selectionEnd;
        const newValue = localValue.substring(0, start) + '  ' + localValue.substring(end);
        setLocalValue(newValue);
        onChange(newValue);
        setTimeout(() => {
          if (textAreaRef.current) {
            textAreaRef.current.selectionStart = textAreaRef.current.selectionEnd = start + 2;
          }
        }, 0);
      }
    }
  };

  const insertMarkdown = (prefix: string, suffix: string = '') => {
    if (!textAreaRef.current) return;

    const start = textAreaRef.current.selectionStart;
    const end = textAreaRef.current.selectionEnd;
    const selectedText = localValue.substring(start, end);
    const newText = localValue.substring(0, start) + prefix + selectedText + suffix + localValue.substring(end);

    setLocalValue(newText);
    onChange(newText);

    setTimeout(() => {
      if (textAreaRef.current) {
        textAreaRef.current.focus();
        const newCursorPos = start + prefix.length + selectedText.length;
        textAreaRef.current.selectionStart = textAreaRef.current.selectionEnd = newCursorPos;
      }
    }, 0);
  };

  return (
    <QuestionWrapper
      question={question}
      value={localValue}
      onChange={onChange}
      onValidate={onValidate}
      disabled={disabled}
      readOnly={readOnly}
      error={error}
      className={className}
    >
      <div className="space-y-2">
        {q.enableMarkdown && !disabled && !readOnly && (
          <div className="flex gap-1 p-1 bg-gray-50 rounded border border-gray-200">
            <button
              type="button"
              onClick={() => insertMarkdown('**', '**')}
              className="px-2 py-1 text-sm hover:bg-white rounded"
              title="Bold"
            >
              <strong>B</strong>
            </button>
            <button
              type="button"
              onClick={() => insertMarkdown('*', '*')}
              className="px-2 py-1 text-sm hover:bg-white rounded"
              title="Italic"
            >
              <em>I</em>
            </button>
            <button
              type="button"
              onClick={() => insertMarkdown('`', '`')}
              className="px-2 py-1 text-sm hover:bg-white rounded font-mono"
              title="Code"
            >
              &lt;/&gt;
            </button>
            <button
              type="button"
              onClick={() => insertMarkdown('- ')}
              className="px-2 py-1 text-sm hover:bg-white rounded"
              title="List"
            >
              â€¢
            </button>
            <button
              type="button"
              onClick={() => insertMarkdown('[', '](url)')}
              className="px-2 py-1 text-sm hover:bg-white rounded"
              title="Link"
            >
              ðŸ”—
            </button>
          </div>
        )}

        <textarea
          ref={textAreaRef}
          value={localValue}
          onChange={handleChange}
          onKeyDown={handleKeyDown}
          placeholder={q.placeholder}
          disabled={disabled}
          readOnly={readOnly}
          rows={q.rows || 4}
          maxLength={q.maxLength}
          className={`
            w-full px-3 py-2 border rounded-md resize-none
            ${disabled || readOnly ? 'bg-gray-100' : 'bg-white'}
            ${error ? 'border-red-500' : 'border-gray-300'}
            focus:outline-none focus:ring-2 focus:ring-blue-500
            ${q.enableMarkdown ? 'font-mono text-sm' : ''}
          `}
          aria-label={q.text}
          aria-required={q.required}
          aria-invalid={!!error}
        />

        <div className="flex justify-between text-xs text-gray-500">
          <span>{wordCount} words</span>
          {q.maxLength && (
            <span>{localValue.length}/{q.maxLength} characters</span>
          )}
        </div>
      </div>
    </QuestionWrapper>
  );
};

export default LongForm;

