  useEffect(() => {
    setLocalValue(value);
  }, [value]);

  const handleChange = (newValue: boolean) => {
    setLocalValue(newValue);
    onChange(newValue);
  };

  const trueLabel = q.trueLabel || 'True';
  const falseLabel = q.falseLabel || 'False';

  return (
    <QuestionWrapper
      question={question}
      value={localValue}
      onChange={onChange}
      onValidate={onValidate}
      disabled={disabled}
      readOnly={readOnly}
      error={error}
      className={className}
