import React, { useState, useEffect } from 'react';
import { QuestionGroup as QuestionGroupType, AnyQuestion, QuestionResponse, Priority } from '../types';
import QuestionRenderer from './QuestionRenderer';

interface QuestionGroupProps {
  group: QuestionGroupType;
  responses: Record<string, QuestionResponse>;
  onQuestionChange: (questionId: string, value: any) => void;
  onGroupComplete?: (groupId: string) => void;
  disabled?: boolean;
  readOnly?: boolean;
  className?: string;
}

const getPriorityBadge = (priority: Priority) => {
  const colors = {
    critical: 'bg-red-100 text-red-800',
    high: 'bg-orange-100 text-orange-800',
    medium: 'bg-yellow-100 text-yellow-800',
    low: 'bg-gray-100 text-gray-800'
  };

  return (
    <span className={`px-2 py-1 text-xs font-medium rounded-full ${colors[priority]}`}>
      {priority}
    </span>
  );
};

export const QuestionGroup: React.FC<QuestionGroupProps> = ({
  group,
  responses,
  onQuestionChange,
  onGroupComplete,
  disabled = false,
  readOnly = false,
  className = ''
}) => {
  const [isExpanded, setIsExpanded] = useState(group.defaultExpanded ?? true);
  const [completedQuestions, setCompletedQuestions] = useState<Set<string>>(new Set());
  const [visibleQuestions, setVisibleQuestions] = useState<Set<string>>(new Set());

  useEffect(() => {
    // Initialize all questions as visible
    const initialVisible = new Set(group.questions.map(q => q.id));
    setVisibleQuestions(initialVisible);
  }, [group.questions]);

  useEffect(() => {
    // Track completed questions
    const completed = new Set<string>();
    group.questions.forEach(question => {
      const response = responses[question.id];
      if (response && response.valid) {
        completed.add(question.id);
      }
    });
    setCompletedQuestions(completed);

    // Check if group is complete
    const requiredQuestions = group.questions.filter(q => q.required && visibleQuestions.has(q.id));
    const allRequiredComplete = requiredQuestions.every(q => completed.has(q.id));

    if (allRequiredComplete && requiredQuestions.length > 0 && onGroupComplete) {
      onGroupComplete(group.id);
    }
  }, [responses, group, visibleQuestions, onGroupComplete]);

  const handleQuestionChange = (questionId: string, value: any) => {
    onQuestionChange(questionId, value);
  };

  const toggleExpand = () => {
    if (group.collapsible) {
      setIsExpanded(!isExpanded);
    }
  };

  const progress = Math.round((completedQuestions.size / group.questions.length) * 100);

  return (
    <div 
      className={`
        question-group 
        border rounded-lg shadow-sm bg-white
        ${className}
      `}
      data-group-id={group.id}
    >
      <div 
        className={`
          flex items-center justify-between p-4 border-b
          ${group.collapsible ? 'cursor-pointer hover:bg-gray-50' : ''}
        `}
        onClick={toggleExpand}
      >
        <div className="flex items-center gap-3">
          {group.collapsible && (
            <button
              type="button"
              className="text-gray-500 hover:text-gray-700 focus:outline-none"
              aria-label={isExpanded ? 'Collapse group' : 'Expand group'}
            >
              <svg 
                className={`w-5 h-5 transform transition-transform ${isExpanded ? 'rotate-90' : ''}`} 
                fill="currentColor" 
                viewBox="0 0 20 20"
              >
                <path fillRule="evenodd" d="M7.293 14.707a1 1 0 010-1.414L10.586 10 7.293 6.707a1 1 0 011.414-1.414l4 4a1 1 0 010 1.414l-4 4a1 1 0 01-1.414 0z" />
              </svg>
            </button>
          )}

          <div>
            <h3 className="text-lg font-semibold text-gray-900">{group.name}</h3>
            {group.description && (
              <p className="text-sm text-gray-600 mt-1">{group.description}</p>
            )}
          </div>
        </div>

        <div className="flex items-center gap-3">
          {group.tags.length > 0 && (
            <div className="flex gap-1">
              {group.tags.map(tag => (
                <span 
                  key={tag}
                  className="px-2 py-1 text-xs bg-gray-100 text-gray-600 rounded-full"
                >
                  {tag}
                </span>
              ))}
            </div>
          )}

          {getPriorityBadge(group.priority)}

          <div className="flex items-center gap-2">
            <div className="text-sm text-gray-600">
              {completedQuestions.size}/{group.questions.length}
            </div>
            <div className="w-24 h-2 bg-gray-200 rounded-full overflow-hidden">
              <div 
                className="h-full bg-green-500 transition-all duration-300"
                style={{ width: `${progress}%` }}
              />
            </div>
          </div>
        </div>
      </div>

      {isExpanded && (
        <div className="p-4 space-y-6">
          {group.questions.map((question, index) => {
            if (!visibleQuestions.has(question.id)) {
              return null;
            }

            const response = responses[question.id];
            const isCompleted = completedQuestions.has(question.id);

            return (
              <div 
                key={question.id}
                className={`
                  relative
                  ${index < group.questions.length - 1 ? 'pb-6 border-b' : ''}
                `}
              >
                {isCompleted && (
                  <div className="absolute -left-8 top-3 text-green-500">
                    âœ“
                  </div>
                )}

                <QuestionRenderer
                  question={question}
                  value={response?.value}
                  onChange={(value) => handleQuestionChange(question.id, value)}
                  disabled={disabled}
                  readOnly={readOnly}
                  error={response?.errors?.[0]}
                />
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default QuestionGroup;

import React from 'react';
import { AnyQuestion, QuestionComponentProps } from '../types';
import ShortAnswer from '../types/ShortAnswer';
import LongForm from '../types/LongForm';
import MultipleChoice from '../types/MultipleChoice';
import TrueFalse from '../types/TrueFalse';
import Slider from '../types/Slider';
import StackRanking from '../types/StackRanking';
import NumericAnswer from '../types/NumericAnswer';

interface QuestionRendererProps extends Omit<QuestionComponentProps, 'question'> {
  question: AnyQuestion;
}

export const QuestionRenderer: React.FC<QuestionRendererProps> = ({
  question,
  value,
  onChange,
  onValidate,
  disabled,
  readOnly,
  error,
  className
}) => {
  const componentProps: QuestionComponentProps = {
    question,
    value,
    onChange,
    onValidate,
    disabled,
    readOnly,
    error,
    className
  };

  switch (question.type) {
    case 'short-answer':
      return <ShortAnswer {...componentProps} />;

    case 'long-form':
      return <LongForm {...componentProps} />;

    case 'multiple-choice':
      return <MultipleChoice {...componentProps} />;

    case 'true-false':
      return <TrueFalse {...componentProps} />;

    case 'slider':
      return <Slider {...componentProps} />;

    case 'stack-ranking':
      return <StackRanking {...componentProps} />;

    case 'numeric':
      return <NumericAnswer {...componentProps} />;

    default:
      console.error(`Unknown question type: ${(question as any).type}`);
      return (
        <div className="p-4 bg-red-50 border border-red-200 rounded-md">
          <p className="text-red-600">Unknown question type: {(question as any).type}</p>
        </div>
      );
  }
};

export default QuestionRenderer;

import { Condition, ConditionOperator, AnyQuestion, QuestionResponse } from '../types';

export class ConditionalLogicEngine {
  private questions: Map<string, AnyQuestion>;
  private responses: Map<string, QuestionResponse>;
  private visibilityCache: Map<string, boolean>;
  private requiredCache: Map<string, boolean>;
  private disabledCache: Map<string, boolean>;

  constructor(questions: AnyQuestion[], responses: Record<string, QuestionResponse>) {
    this.questions = new Map(questions.map(q => [q.id, q]));
    this.responses = new Map(Object.entries(responses));
    this.visibilityCache = new Map();
    this.requiredCache = new Map();
    this.disabledCache = new Map();
  }

  /**
   * Evaluate a condition against a response value
   */
  private evaluateCondition(condition: Condition, value: any): boolean {
    switch (condition.operator) {
      case 'equals':
        return value === condition.value;

      case 'not-equals':
        return value !== condition.value;

      case 'contains':
        if (typeof value === 'string') {
          return value.includes(condition.value);
        }
        if (Array.isArray(value)) {
          return value.includes(condition.value);
        }
        return false;

      case 'not-contains':
        if (typeof value === 'string') {
          return !value.includes(condition.value);
        }
        if (Array.isArray(value)) {
          return !value.includes(condition.value);
        }
        return true;

      case 'greater-than':
        return typeof value === 'number' && value > condition.value;

      case 'less-than':
        return typeof value === 'number' && value < condition.value;

      case 'greater-than-or-equal':
        return typeof value === 'number' && value >= condition.value;

      case 'less-than-or-equal':
        return typeof value === 'number' && value <= condition.value;

      case 'in':
        return Array.isArray(condition.value) && condition.value.includes(value);

      case 'not-in':
        return Array.isArray(condition.value) && !condition.value.includes(value);

      case 'is-empty':
        if (value === null || value === undefined || value === '') return true;
        if (Array.isArray(value)) return value.length === 0;
        if (typeof value === 'string') return value.trim() === '';
        return false;

      case 'is-not-empty':
        if (value === null || value === undefined || value === '') return false;
        if (Array.isArray(value)) return value.length > 0;
        if (typeof value === 'string') return value.trim() !== '';
        return true;

      default:
        console.warn(`Unknown condition operator: ${condition.operator}`);
        return false;
    }
  }

  /**
   * Get the effective state of a question based on conditions
   */
  getQuestionState(questionId: string): {
    visible: boolean;
    required: boolean;
    disabled: boolean;
  } {
    const question = this.questions.get(questionId);
    if (!question) {
      return { visible: true, required: false, disabled: false };
    }

    // Check cached values first
    if (this.visibilityCache.has(questionId)) {
      return {
        visible: this.visibilityCache.get(questionId)!,
        required: this.requiredCache.get(questionId)!,
        disabled: this.disabledCache.get(questionId)!
      };
    }

    let visible = true;
    let required = question.required;
    let disabled = false;

    if (question.conditions && question.conditions.length > 0) {
      for (const condition of question.conditions) {
        const dependentResponse = this.responses.get(condition.questionId);
        const dependentValue = dependentResponse?.value;
        const conditionMet = this.evaluateCondition(condition, dependentValue);

        switch (condition.action) {
          case 'show':
            if (!conditionMet) visible = false;
            break;

          case 'hide':
            if (conditionMet) visible = false;
            break;

          case 'require':
            if (conditionMet) required = true;
            break;

          case 'disable':
            if (conditionMet) disabled = true;
            break;

          case 'enable':
            if (!conditionMet) disabled = true;
            break;
        }
      }
    }

    // Cache the results
    this.visibilityCache.set(questionId, visible);
    this.requiredCache.set(questionId, required);
    this.disabledCache.set(questionId, disabled);

    return { visible, required, disabled };
  }

  /**
   * Get all questions that should be visible
   */
  getVisibleQuestions(): Set<string> {
    const visible = new Set<string>();

    for (const question of this.questions.values()) {
      const state = this.getQuestionState(question.id);
      if (state.visible) {
        visible.add(question.id);
      }
    }

    return visible;
  }

  /**
   * Get all questions that depend on a given question
   */
  getDependentQuestions(questionId: string): Set<string> {
    const dependents = new Set<string>();

    for (const question of this.questions.values()) {
      if (question.conditions) {
        for (const condition of question.conditions) {
          if (condition.questionId === questionId) {
            dependents.add(question.id);
            // Recursively get dependents of dependents
            const nestedDependents = this.getDependentQuestions(question.id);
            nestedDependents.forEach(id => dependents.add(id));
          }
        }
      }
    }

    return dependents;
  }

  /**
   * Clear cache for a question and its dependents when a value changes
   */
  clearCache(questionId: string) {
    // Clear cache for the question itself
    this.visibilityCache.delete(questionId);
    this.requiredCache.delete(questionId);
    this.disabledCache.delete(questionId);

    // Clear cache for all dependent questions
    const dependents = this.getDependentQuestions(questionId);
    dependents.forEach(id => {
      this.visibilityCache.delete(id);
      this.requiredCache.delete(id);
      this.disabledCache.delete(id);
    });
  }

  /**
   * Update a response and recalculate states
   */
  updateResponse(questionId: string, response: QuestionResponse) {
    this.responses.set(questionId, response);
    this.clearCache(questionId);
  }

  /**
   * Get the evaluation path for debugging
   */
  getEvaluationPath(questionId: string): string[] {
    const path: string[] = [];
    const question = this.questions.get(questionId);

    if (!question || !question.conditions) return path;

    for (const condition of question.conditions) {
      const dependentResponse = this.responses.get(condition.questionId);
      const dependentQuestion = this.questions.get(condition.questionId);
      const conditionMet = this.evaluateCondition(condition, dependentResponse?.value);

      path.push(
        `${dependentQuestion?.text || condition.questionId} ${condition.operator} ${condition.value} â†’ ${condition.action} (${conditionMet ? 'MET' : 'NOT MET'})`
      );
    }

    return path;
  }
}

export default ConditionalLogicEngine;

import React, { useState, useEffect } from 'react';
import { ShortAnswerQuestion, QuestionComponentProps } from '../types';
import QuestionWrapper from '../core/QuestionWrapper';

export const ShortAnswer: React.FC<QuestionComponentProps<string>> = ({
  question,
  value = '',
  onChange,
  onValidate,
  disabled = false,
  readOnly = false,
  error,
  className = ''
}) => {
  const q = question as ShortAnswerQuestion;
  const [localValue, setLocalValue] = useState(value);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [filteredSuggestions, setFilteredSuggestions] = useState<string[]>([]);

  useEffect(() => {
    setLocalValue(value);
  }, [value]);

  useEffect(() => {
    if (q.suggestions && localValue && !disabled && !readOnly) {
      const filtered = q.suggestions.filter(s => 
        s.toLowerCase().includes(localValue.toLowerCase())
      );
      setFilteredSuggestions(filtered);
      setShowSuggestions(filtered.length > 0);
    } else {
      setShowSuggestions(false);
    }
  }, [localValue, q.suggestions, disabled, readOnly]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const newValue = e.target.value;
    if (q.maxLength && newValue.length > q.maxLength) return;

    setLocalValue(newValue);
    onChange(newValue);
  };

  const handleSuggestionClick = (suggestion: string) => {
    setLocalValue(suggestion);
    onChange(suggestion);
    setShowSuggestions(false);
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Escape') {
      setShowSuggestions(false);
    }
  };

  return (
    <QuestionWrapper
      question={question}
      value={localValue}
      onChange={onChange}
      onValidate={onValidate}
      disabled={disabled}
      readOnly={readOnly}
      error={error}
      className={className}
    >
      <div className="relative">
        <input
          type="text"
          value={localValue}
          onChange={handleChange}
          onKeyDown={handleKeyDown}
          onFocus={() => q.suggestions && setShowSuggestions(true)}
          onBlur={() => setTimeout(() => setShowSuggestions(false), 200)}
          placeholder={q.placeholder}
          disabled={disabled}
          readOnly={readOnly}
          pattern={q.pattern}
          maxLength={q.maxLength}
          className={`
            w-full px-3 py-2 border rounded-md
            ${disabled || readOnly ? 'bg-gray-100' : 'bg-white'}
            ${error ? 'border-red-500' : 'border-gray-300'}
            focus:outline-none focus:ring-2 focus:ring-blue-500
          `}
          aria-label={q.text}
          aria-required={q.required}
          aria-invalid={!!error}
        />

        {q.maxLength && (
          <div className="absolute right-2 top-2 text-xs text-gray-500">
            {localValue.length}/{q.maxLength}
          </div>
        )}

        {showSuggestions && filteredSuggestions.length > 0 && (
          <div className="absolute z-10 w-full mt-1 bg-white border border-gray-300 rounded-md shadow-lg max-h-60 overflow-y-auto">
            {filteredSuggestions.map((suggestion, index) => (
              <button
                key={index}
                type="button"
                onClick={() => handleSuggestionClick(suggestion)}
                className="w-full px-3 py-2 text-left hover:bg-blue-50 focus:bg-blue-50 focus:outline-none"
              >
                {suggestion}
              </button>
            ))}
          </div>
        )}
      </div>
    </QuestionWrapper>
  );
};

export default ShortAnswer;

import React, { useState, useEffect, useRef } from 'react';
import { LongFormQuestion, QuestionComponentProps } from '../types';
import QuestionWrapper from '../core/QuestionWrapper';

export const LongForm: React.FC<QuestionComponentProps<string>> = ({
  question,
  value = '',
  onChange,
  onValidate,
  disabled = false,
  readOnly = false,
  error,
  className = ''
}) => {
  const q = question as LongFormQuestion;
  const [localValue, setLocalValue] = useState(value);
  const [wordCount, setWordCount] = useState(0);
  const textAreaRef = useRef<HTMLTextAreaElement>(null);

  useEffect(() => {
    setLocalValue(value);
    updateWordCount(value);
  }, [value]);

  useEffect(() => {
    adjustTextAreaHeight();
  }, [localValue]);

  const updateWordCount = (text: string) => {
    const words = text.trim().split(/\s+/).filter(word => word.length > 0);
    setWordCount(words.length);
  };

  const adjustTextAreaHeight = () => {
    if (textAreaRef.current) {
      textAreaRef.current.style.height = 'auto';
      textAreaRef.current.style.height = `${textAreaRef.current.scrollHeight}px`;
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    const newValue = e.target.value;

    if (q.maxLength && newValue.length > q.maxLength) return;

    setLocalValue(newValue);
    updateWordCount(newValue);
    onChange(newValue);
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLTextAreaElement>) => {
    if (q.enableMarkdown) {
      if (e.key === 'Tab') {
        e.preventDefault();
        const start = e.currentTarget.selectionStart;
        const end = e.currentTarget.selectionEnd;
        const newValue = localValue.substring(0, start) + '  ' + localValue.substring(end);
        setLocalValue(newValue);
        onChange(newValue);
        setTimeout(() => {
          if (textAreaRef.current) {
            textAreaRef.current.selectionStart = textAreaRef.current.selectionEnd = start + 2;
          }
        }, 0);
      }
    }
  };

  const insertMarkdown = (prefix: string, suffix: string = '') => {
    if (!textAreaRef.current) return;

    const start = textAreaRef.current.selectionStart;
    const end = textAreaRef.current.selectionEnd;
    const selectedText = localValue.substring(start, end);
    const newText = localValue.substring(0, start) + prefix + selectedText + suffix + localValue.substring(end);

    setLocalValue(newText);
    onChange(newText);

    setTimeout(() => {
      if (textAreaRef.current) {
        textAreaRef.current.focus();
        const newCursorPos = start + prefix.length + selectedText.length;
        textAreaRef.current.selectionStart = textAreaRef.current.selectionEnd = newCursorPos;
      }
    }, 0);
  };

  return (
    <QuestionWrapper
      question={question}
      value={localValue}
      onChange={onChange}
      onValidate={onValidate}
      disabled={disabled}
      readOnly={readOnly}
      error={error}
      className={className}
    >
      <div className="space-y-2">
        {q.enableMarkdown && !disabled && !readOnly && (
          <div className="flex gap-1 p-1 bg-gray-50 rounded border border-gray-200">
            <button
              type="button"
              onClick={() => insertMarkdown('**', '**')}
              className="px-2 py-1 text-sm hover:bg-white rounded"
              title="Bold"
            >
              <strong>B</strong>
            </button>
            <button
              type="button"
              onClick={() => insertMarkdown('*', '*')}
              className="px-2 py-1 text-sm hover:bg-white rounded"
              title="Italic"
            >
              <em>I</em>
            </button>
            <button
              type="button"
              onClick={() => insertMarkdown('`', '`')}
              className="px-2 py-1 text-sm hover:bg-white rounded font-mono"
              title="Code"
            >
              &lt;/&gt;
            </button>
            <button
              type="button"
              onClick={() => insertMarkdown('- ')}
              className="px-2 py-1 text-sm hover:bg-white rounded"
              title="List"
            >
              â€¢
            </button>
            <button
              type="button"
              onClick={() => insertMarkdown('[', '](url)')}
              className="px-2 py-1 text-sm hover:bg-white rounded"
              title="Link"
            >
              ðŸ”—
            </button>
          </div>
        )}

        <textarea
          ref={textAreaRef}
          value={localValue}
          onChange={handleChange}
          onKeyDown={handleKeyDown}
          placeholder={q.placeholder}
          disabled={disabled}
          readOnly={readOnly}
          rows={q.rows || 4}
          maxLength={q.maxLength}
          className={`
            w-full px-3 py-2 border rounded-md resize-none
            ${disabled || readOnly ? 'bg-gray-100' : 'bg-white'}
            ${error ? 'border-red-500' : 'border-gray-300'}
            focus:outline-none focus:ring-2 focus:ring-blue-500
            ${q.enableMarkdown ? 'font-mono text-sm' : ''}
          `}
          aria-label={q.text}
          aria-required={q.required}
          aria-invalid={!!error}
        />

        <div className="flex justify-between text-xs text-gray-500">
          <span>{wordCount} words</span>
          {q.maxLength && (
            <span>{localValue.length}/{q.maxLength} characters</span>
          )}
        </div>
      </div>
    </QuestionWrapper>
  );
};

