import React, { useState, useEffect } from 'react';
import {
  QuestionRenderer,
  QuestionGroup,
  ConditionalLogicEngine,
  AnyQuestion,
  QuestionGroup as QuestionGroupType,
  QuestionResponse,
  FormState
} from '../ui/components/questions';

const QuestionsDemo: React.FC = () => {
  const [formState, setFormState] = useState<FormState>({
    responses: {},
    currentGroup: undefined,
    completedGroups: [],
    errors: {},
    isDirty: false,
    isSubmitting: false,
    isValid: false
  });

  const [conditionalEngine, setConditionalEngine] = useState<ConditionalLogicEngine | null>(null);
  const [visibleQuestions, setVisibleQuestions] = useState<Set<string>>(new Set());

  // Sample questions for demonstration
  const sampleQuestions: AnyQuestion[] = [
    {
      id: 'name',
      type: 'short-answer',
      text: 'What is your name?',
      description: 'Please enter your full name',
      placeholder: 'John Doe',
      required: true,
      priority: 'high',
      tags: ['personal'],
      maxLength: 100,
      suggestions: ['Alice Smith', 'Bob Johnson', 'Carol Williams']
    },
    {
      id: 'bio',
      type: 'long-form',
