import { describe, it, expect, vi } from 'vitest';
import { screen, fireEvent } from '@testing-library/react';
import { ShortAnswer } from '../../types/ShortAnswer';
import { 
  renderWithProviders, 
  createMockShortAnswerQuestion, 
  user 
} from '../test-utils';

describe('ShortAnswer Component', () => {
  it('renders with correct props', () => {
    const question = createMockShortAnswerQuestion({
      text: 'Test question',
      description: 'Test description'
    });

    renderWithProviders(
      <ShortAnswer 
        question={question}
        value=""
        onChange={() => {}}
      />
    );

    expect(screen.getByLabelText('Test question')).toBeInTheDocument();
    expect(screen.getByText('Test description')).toBeInTheDocument();
  });

  it('handles text input changes', async () => {
    const onChange = vi.fn();
    const question = createMockShortAnswerQuestion();

    renderWithProviders(
      <ShortAnswer 
        question={question}
        value=""
        onChange={onChange}
      />
    );

    const input = screen.getByRole('textbox');
    await user.type(input, 'Hello World');

    expect(onChange).toHaveBeenCalledTimes(11); // Once for each character
    expect(onChange).toHaveBeenLastCalledWith('Hello World');
  });

  it('enforces maxLength constraint', async () => {
    const onChange = vi.fn();
    const question = createMockShortAnswerQuestion({
      maxLength: 5
    });

    renderWithProviders(
      <ShortAnswer 
        question={question}
        value=""
        onChange={onChange}
      />
    );

    const input = screen.getByRole('textbox');
    await user.type(input, 'Hello World');

    // Should only accept first 5 characters
    expect(onChange).toHaveBeenLastCalledWith('Hello');
  });

  it('shows character count when maxLength is set', () => {
    const question = createMockShortAnswerQuestion({
      maxLength: 100
    });

    renderWithProviders(
      <ShortAnswer 
        question={question}
        value="Hello"
        onChange={() => {}}
      />
    );

    expect(screen.getByText('5/100')).toBeInTheDocument();
  });

  it('displays placeholder text', () => {
    const question = createMockShortAnswerQuestion({
      placeholder: 'Enter your name here'
    });

    renderWithProviders(
      <ShortAnswer 
        question={question}
        value=""
        onChange={() => {}}
      />
    );

    const input = screen.getByPlaceholderText('Enter your name here');
    expect(input).toBeInTheDocument();
  });

  it('shows suggestions dropdown when available', async () => {
    const question = createMockShortAnswerQuestion({
      suggestions: ['Apple', 'Banana', 'Cherry']
    });

    renderWithProviders(
      <ShortAnswer 
        question={question}
        value=""
        onChange={() => {}}
      />
    );

    const input = screen.getByRole('textbox');
    await user.click(input);

    expect(screen.getByText('Apple')).toBeInTheDocument();
    expect(screen.getByText('Banana')).toBeInTheDocument();
    expect(screen.getByText('Cherry')).toBeInTheDocument();
  });

  it('filters suggestions based on input', async () => {
    const onChange = vi.fn();
    const question = createMockShortAnswerQuestion({
      suggestions: ['Apple', 'Banana', 'Cherry', 'Apricot']
    });

    renderWithProviders(
      <ShortAnswer 
        question={question}
        value=""
        onChange={onChange}
      />
    );

    const input = screen.getByRole('textbox');
    await user.type(input, 'Ap');

    expect(screen.getByText('Apple')).toBeInTheDocument();
    expect(screen.getByText('Apricot')).toBeInTheDocument();
    expect(screen.queryByText('Banana')).not.toBeInTheDocument();
    expect(screen.queryByText('Cherry')).not.toBeInTheDocument();
  });

  it('selects suggestion on click', async () => {
    const onChange = vi.fn();
    const question = createMockShortAnswerQuestion({
      suggestions: ['Apple', 'Banana', 'Cherry']
    });

    renderWithProviders(
      <ShortAnswer 
        question={question}
        value=""
        onChange={onChange}
      />
    );

    const input = screen.getByRole('textbox');
    await user.click(input);

    const appleSuggestion = screen.getByText('Apple');
    await user.click(appleSuggestion);

    expect(onChange).toHaveBeenCalledWith('Apple');
  });

  it('validates against pattern when provided', () => {
    const question = createMockShortAnswerQuestion({
      pattern: '^[a-zA-Z]+$'
    });

    const { container } = renderWithProviders(
      <ShortAnswer 
        question={question}
        value="Test123"
        onChange={() => {}}
      />
    );

    const input = container.querySelector('input[pattern="^[a-zA-Z]+$"]');
    expect(input).toBeInTheDocument();
  });

  it('handles disabled state', () => {
    const question = createMockShortAnswerQuestion();

    renderWithProviders(
      <ShortAnswer 
        question={question}
        value=""
        onChange={() => {}}
        disabled={true}
      />
    );

    const input = screen.getByRole('textbox');
    expect(input).toBeDisabled();
  });

  it('handles readOnly state', () => {
    const question = createMockShortAnswerQuestion();

    renderWithProviders(
      <ShortAnswer 
        question={question}
        value="Test Value"
        onChange={() => {}}
        readOnly={true}
      />
    );

    const input = screen.getByRole('textbox');
    expect(input).toHaveAttribute('readonly');
  });

  it('shows error state', () => {
    const question = createMockShortAnswerQuestion();

    renderWithProviders(
      <ShortAnswer 
        question={question}
        value=""
        onChange={() => {}}
        error="This field is required"
      />
    );

    expect(screen.getByText('This field is required')).toBeInTheDocument();
  });

  it('closes suggestions on Escape key', async () => {
    const question = createMockShortAnswerQuestion({
      suggestions: ['Apple', 'Banana', 'Cherry']
    });

    renderWithProviders(
      <ShortAnswer 
        question={question}
        value=""
        onChange={() => {}}
      />
    );

    const input = screen.getByRole('textbox');
    await user.click(input);
    expect(screen.getByText('Apple')).toBeInTheDocument();

    fireEvent.keyDown(input, { key: 'Escape' });
    expect(screen.queryByText('Apple')).not.toBeInTheDocument();
  });
});

