import { describe, it, expect, vi } from 'vitest';
import { screen } from '@testing-library/react';
import { LongForm } from '../../types/LongForm';
import { 
  renderWithProviders, 
  createMockLongFormQuestion, 
  user 
} from '../test-utils';

describe('LongForm Component', () => {
  it('renders textarea with correct props', () => {
    const question = createMockLongFormQuestion({
      text: 'Tell us about yourself',
      description: 'Share your background',
      rows: 5
    });

    renderWithProviders(
      <LongForm 
        question={question}
        value=""
        onChange={() => {}}
      />
    );

    expect(screen.getByLabelText('Tell us about yourself')).toBeInTheDocument();
    expect(screen.getByText('Share your background')).toBeInTheDocument();
    const textarea = screen.getByRole('textbox');
    expect(textarea.tagName).toBe('TEXTAREA');
  });

  it('handles text input changes', async () => {
    const onChange = vi.fn();
    const question = createMockLongFormQuestion();

    renderWithProviders(
      <LongForm 
        question={question}
        value=""
        onChange={onChange}
      />
    );

    const textarea = screen.getByRole('textbox');
    await user.type(textarea, 'This is a long form response');

    expect(onChange).toHaveBeenLastCalledWith('This is a long form response');
  });

  it('shows word count', () => {
    const question = createMockLongFormQuestion();

    renderWithProviders(
      <LongForm 
        question={question}
        value="Hello world this is a test"
        onChange={() => {}}
      />
    );

    expect(screen.getByText('6 words')).toBeInTheDocument();
  });

  it('enforces maxLength constraint', async () => {
    const onChange = vi.fn();
    const question = createMockLongFormQuestion({
      maxLength: 10
    });

    renderWithProviders(
      <LongForm 
        question={question}
        value=""
        onChange={onChange}
      />
    );

    const textarea = screen.getByRole('textbox');
    await user.type(textarea, 'Hello World Test');

    // Should only accept first 10 characters
    expect(onChange).toHaveBeenLastCalledWith('Hello Worl');
  });

  it('shows character count when maxLength is set', () => {
    const question = createMockLongFormQuestion({
      maxLength: 500
    });

    renderWithProviders(
      <LongForm 
        question={question}
        value="Hello"
        onChange={() => {}}
      />
    );

    expect(screen.getByText('5/500 characters')).toBeInTheDocument();
  });

  it('shows markdown toolbar when enabled', () => {
    const question = createMockLongFormQuestion({
      enableMarkdown: true
    });

    renderWithProviders(
      <LongForm 
        question={question}
        value=""
        onChange={() => {}}
      />
    );

    // Check for markdown buttons
    expect(screen.getByTitle('Bold')).toBeInTheDocument();
    expect(screen.getByTitle('Italic')).toBeInTheDocument();
    expect(screen.getByTitle('Code')).toBeInTheDocument();
    expect(screen.getByTitle('List')).toBeInTheDocument();
    expect(screen.getByTitle('Link')).toBeInTheDocument();
  });

  it('inserts bold markdown formatting', async () => {
    const onChange = vi.fn();
    const question = createMockLongFormQuestion({
      enableMarkdown: true
    });

    renderWithProviders(
      <LongForm 
        question={question}
        value="Hello World"
        onChange={onChange}
      />
    );

    const textarea = screen.getByRole('textbox') as HTMLTextAreaElement;

    // Select "World"
    textarea.setSelectionRange(6, 11);

    const boldButton = screen.getByTitle('Bold');
    await user.click(boldButton);

    expect(onChange).toHaveBeenCalledWith('Hello **World**');
  });

  it('inserts italic markdown formatting', async () => {
    const onChange = vi.fn();
    const question = createMockLongFormQuestion({
      enableMarkdown: true
    });

    renderWithProviders(
      <LongForm 
        question={question}
        value="Hello World"
        onChange={onChange}
      />
    );

    const textarea = screen.getByRole('textbox') as HTMLTextAreaElement;
    textarea.setSelectionRange(6, 11);

    const italicButton = screen.getByTitle('Italic');
    await user.click(italicButton);

    expect(onChange).toHaveBeenCalledWith('Hello *World*');
  });

  it('handles Tab key to insert spaces in markdown mode', async () => {
    const onChange = vi.fn();
    const question = createMockLongFormQuestion({
      enableMarkdown: true
    });

    renderWithProviders(
      <LongForm 
        question={question}
        value="Hello"
        onChange={onChange}
      />
    );

    const textarea = screen.getByRole('textbox');
    await user.click(textarea);
    await user.tab();

    // Tab should insert spaces in markdown mode
    expect(onChange).toHaveBeenCalledWith(expect.stringContaining('  '));
  });

  it('auto-resizes based on content', async () => {
    const question = createMockLongFormQuestion();

    renderWithProviders(
      <LongForm 
        question={question}
        value=""
        onChange={() => {}}
      />
    );

    const textarea = screen.getByRole('textbox') as HTMLTextAreaElement;
    const initialHeight = textarea.scrollHeight;

    // Add multiple lines of text
    await user.type(textarea, 'Line 1\nLine 2\nLine 3\nLine 4\nLine 5');

    // Height should have increased
    expect(textarea.scrollHeight).toBeGreaterThan(initialHeight);
  });

  it('handles disabled state', () => {
    const question = createMockLongFormQuestion();

    renderWithProviders(
      <LongForm 
        question={question}
        value=""
        onChange={() => {}}
        disabled={true}
      />
    );

    const textarea = screen.getByRole('textbox');
    expect(textarea).toBeDisabled();
  });

  it('handles readOnly state', () => {
    const question = createMockLongFormQuestion();

    renderWithProviders(
      <LongForm 
        question={question}
        value="Test Value"
        onChange={() => {}}
        readOnly={true}
      />
    );

    const textarea = screen.getByRole('textbox');
    expect(textarea).toHaveAttribute('readonly');
  });

  it('hides markdown toolbar when disabled', () => {
    const question = createMockLongFormQuestion({
      enableMarkdown: true
    });

    renderWithProviders(
      <LongForm 
        question={question}
        value=""
        onChange={() => {}}
        disabled={true}
      />
    );

    // Markdown toolbar should not be shown when disabled
    expect(screen.queryByTitle('Bold')).not.toBeInTheDocument();
  });
});

