        value="opt1"
        onChange={() => {}}
        readOnly={true}
      />
    );

    const inputs = screen.getAllByRole('radio');
    inputs.forEach(input => {
      expect(input).toBeDisabled();
    });
  });

  it('shows selected state correctly', () => {
    const question = createMockMultipleChoiceQuestion({
      multiple: false
    });

    renderWithProviders(
      <MultipleChoice 
        question={question}
        value="js"
        onChange={() => {}}
      />
    );

    const selectedOption = screen.getByLabelText('JavaScript') as HTMLInputElement;
    expect(selectedOption.checked).toBe(true);

    const unselectedOption = screen.getByLabelText('TypeScript') as HTMLInputElement;
    expect(unselectedOption.checked).toBe(false);
  });

  it('shows multiple selected states correctly', () => {
    const question = createMockMultipleChoiceQuestion({
      multiple: true
    });

    renderWithProviders(
      <MultipleChoice 
        question={question}
        value={['js', 'python']}
        onChange={() => {}}
      />
    );

    const jsOption = screen.getByLabelText('JavaScript') as HTMLInputElement;
    const tsOption = screen.getByLabelText('TypeScript') as HTMLInputElement;
    const pythonOption = screen.getByLabelText('Python') as HTMLInputElement;

    expect(jsOption.checked).toBe(true);
    expect(tsOption.checked).toBe(false);
    expect(pythonOption.checked).toBe(true);
  });
});

