          {
            questionId: 'q1',
            operator: 'equals',
            value: 'John',
            action: 'show'
          },
          {
            questionId: 'q2',
            operator: 'greater-than',
            value: 18,
            action: 'require'
          }
        ]
      });
      questions[0].text = 'Name';
      questions[1].text = 'Age';
      questions.push(question);
      responses['q1'] = createMockResponse('q1', 'Jane');
      responses['q2'] = createMockResponse('q2', 25);

      engine = new ConditionalLogicEngine(questions, responses);
      const path = engine.getEvaluationPath('q5');

      expect(path).toHaveLength(2);
      expect(path[0]).toContain('Name equals John â†’ show (NOT MET)');
      expect(path[1]).toContain('Age greater-than 18 â†’ require (MET)');
    });
  });
});

