export const createMockResponse = <T = any>(questionId: string, value: T): QuestionResponse<T> => ({
  questionId,
  value,
  timestamp: new Date(),
  valid: true,
  errors: []
});

// Helper to wait for async updates
export const waitFor = (callback: () => boolean | void, timeout = 1000): Promise<void> => {
  return new Promise((resolve, reject) => {
    const startTime = Date.now();
    const interval = setInterval(() => {
      if (callback()) {
        clearInterval(interval);
        resolve();
      }
      if (Date.now() - startTime > timeout) {
        clearInterval(interval);
        reject(new Error('Timeout waiting for condition'));
      }
    }, 50);
  });
};

