  type: 'numeric';
  min?: number;
  max?: number;
  step?: number;
  precision?: number;
  unit?: string;
  showAsPercentage?: boolean;
}

export type AnyQuestion = 
  | ShortAnswerQuestion
  | LongFormQuestion
  | MultipleChoiceQuestion
  | TrueFalseQuestion
  | SliderQuestion
  | StackRankingQuestion
  | NumericQuestion;

export interface QuestionGroup {
  id: string;
