  value: T;
  timestamp: Date;
  valid: boolean;
  errors?: string[];
}

export interface FormState {
  responses: Record<string, QuestionResponse>;
  currentGroup?: string;
  completedGroups: string[];
  errors: Record<string, string[]>;
  isDirty: boolean;
  isSubmitting: boolean;
  isValid: boolean;
}

export interface QuestionComponentProps<T = any> {
