import React from 'react';
import type { AnyQuestion, QuestionComponentProps } from '../types';
import ShortAnswer from '../types/ShortAnswer';
import LongForm from '../types/LongForm';
import MultipleChoice from '../types/MultipleChoice';
import TrueFalse from '../types/TrueFalse';
import Slider from '../types/Slider';
import StackRanking from '../types/StackRanking';
import NumericAnswer from '../types/NumericAnswer';

interface QuestionRendererProps extends Omit<QuestionComponentProps, 'question'> {
  question: AnyQuestion;
}

export const QuestionRenderer: React.FC<QuestionRendererProps> = ({
  question,
  value,
  onChange,
  onValidate,
  disabled,
  readOnly,
  error,
  className
}) => {
  const componentProps: QuestionComponentProps = {
    question,
    value,
    onChange,
    onValidate,
    disabled,
    readOnly,
    error,
    className
  };

  switch (question.type) {
    case 'short-answer':
      return <ShortAnswer {...componentProps} />;

    case 'long-form':
      return <LongForm {...componentProps} />;

    case 'multiple-choice':
      return <MultipleChoice {...componentProps} />;

    case 'true-false':
      return <TrueFalse {...componentProps} />;

    case 'slider':
      return <Slider {...componentProps} />;

    case 'stack-ranking':
      return <StackRanking {...componentProps} />;

    case 'numeric':
      return <NumericAnswer {...componentProps} />;

    default:
      console.error(`Unknown question type: ${(question as any).type}`);
      return (
        <div className="p-4 bg-red-50 border border-red-200 rounded-md">
          <p className="text-red-600">Unknown question type: {(question as any).type}</p>
        </div>
      );
  }
};

export default QuestionRenderer;

