      priority: 'low',
      tags: ['preferences'],
      min: 0,
      max: 20,
      step: 0.5,
      dual: false,
      showValue: true,
      unit: ' years',
      marks: [
        { value: 0, label: 'None' },
        { value: 5, label: '5' },
        { value: 10, label: '10' },
        { value: 15, label: '15' },
        { value: 20, label: '20+' }
      ],
      conditions: [
        {
          questionId: 'remote',
          operator: 'equals',
          value: true,
          action: 'show'
        }
      ]
    },
    {
      id: 'salary-range',
      type: 'slider',
      text: 'What is your expected salary range?',
