#!/usr/bin/env bun

import { existsSync, mkdirSync, readFileSync, writeFileSync } from 'node:fs';
import { join } from 'node:path';
import { createGeminiLLM } from '@lasercat/homogenaize';
import { z } from 'zod';

/**
 * Claude hook script that logs all payloads to a JSONL file
 * This script captures hook events and writes them to ./tmp/hooks.jsonl
 */

const args = process.argv.slice(2);
const hookType = process.env.CLAUDE_HOOK_TYPE || 'unknown';

// Ensure tmp directory exists
const TMP_DIR = join(process.cwd(), '');
if (!existsSync(TMP_DIR)) {
  mkdirSync(TMP_DIR, { recursive: true });
}

// Define the structured summary schema
const ConversationSummarySchema = z.object({
  briefSummary: z.string()
    .describe('A concise 2-3 sentence overview of the entire conversation'),

  mainTopics: z.array(z.string())
    .describe('List of main topics or themes discussed in the conversation'),

  userQuestions: z.array(z.object({
    question: z.string(),
    answered: z.boolean(),
    answerSummary: z.string().optional()
  })).describe('Questions asked by the user and whether they were answered'),

  keyInformation: z.array(z.object({
    topic: z.string(),
    details: z.string()
  })).describe('Important information, solutions, or explanations provided'),

  codeSnippets: z.array(z.object({
    language: z.string(),
    purpose: z.string(),
    mentioned: z.boolean()
  })).optional().describe('Any code examples discussed or provided'),

  actionItems: z.array(z.string())
    .optional()
    .describe('Any next steps, todos, or actions mentioned'),

  resolution: z.enum(['fully_resolved', 'partially_resolved', 'unresolved', 'informational'])
    .describe('Whether the user\'s needs were addressed'),

  tags: z.array(z.string())
    .describe('Relevant tags or keywords for categorization')
});

// Type inference for the summary
type ConversationSummary = z.infer<typeof ConversationSummarySchema>;

const readStdin = async (): Promise<string> => {
  const chunks: Buffer[] = [];

  for await (const chunk of process.stdin) {
    chunks.push(chunk);
  }

  return Buffer.concat(chunks).toString('utf8');
};

/**
 * Parse JSONL transcript and extract only user and assistant messages
 */
interface Message {
  type: 'user' | 'assistant' | 'system';
  message?: {
    content?: string | Array<{ type: string; text: string }>;
  };
}

function extractMessages(transcriptContent: string): Message[] {
  const lines = transcriptContent.trim().split('\n');
  const messages = [];

  for (const line of lines) {
    if (!line.trim()) continue;

    try {
      const obj = JSON.parse(line);

      // Only include messages from user or assistant, not summaries
      if (obj.type === 'user' || obj.type === 'assistant') {
        messages.push(obj);
      }
    } catch (err) {
      console.error('Error parsing line:', err);
      console.error('Line content:', line);
    }
  }

  return messages;
}

/**
 * Format messages for LLM consumption
 */
function formatMessagesForLLM(messages: Message[]): Array<{ role: 'user' | 'assistant' | 'system'; content: string }> {
  const formattedMessages = [];

  for (const msg of messages) {
    if (msg.type === 'user') {
      const content = msg.message?.content || '';
      formattedMessages.push({ role: 'user' as const, content });
    } else if (msg.type === 'assistant') {
      let content = '';
      if (msg.message?.content) {
        if (Array.isArray(msg.message.content)) {
          content = msg.message.content
            .filter((item) => item.type === 'text')
            .map((item) => item.text)
            .join('\n');
        } else {
          content = msg.message.content;
        }
      }
      formattedMessages.push({ role: 'assistant' as const, content });
    }
  }

  return formattedMessages;
}

/**
 * Generate structured summary using the complex schema with enums
 * Now works with homogenaize v1.2.4!
 */
async function generateStructuredSummary(messages: Message[]): Promise<ConversationSummary | null> {
  const apiKey = process.env.GEMINI_API_KEY;

  if (!apiKey) {
    console.warn('GEMINI_API_KEY not set, skipping summary generation');
    return null;
  }

  try {
    const gemini = createGeminiLLM({
      apiKey,
      model: 'gemini-2.5-pro',
      logging: true,
    });

    const conversationMessages = formatMessagesForLLM(messages);

    const response = await gemini.chat({
      messages: [
        { 
          role: 'system', 
          content: 'You are an AI assistant that analyzes conversations and creates structured summaries.' 
        },
        ...conversationMessages,
        { 
          role: 'user', 
          content: 'Please analyze this conversation and provide a structured summary.' 
        }
      ],
      schema: ConversationSummarySchema,
      temperature: 0.3,
      maxTokens: 1500,
    });

    console.log('âœ… Successfully generated structured summary with enums!');
    console.log('Response type:', typeof response.content);

    return response.content as ConversationSummary;

  } catch (error) {
    console.error('Error generating structured summary:', error);
    if (error instanceof z.ZodError) {
      console.error('Validation errors:', error.issues);
    }
    return null;
  }
}

async function main() {
  try {
    // Read and log stdin
    const stdin = await readStdin();

    // Optionally write stdin content to a file as well
    if (stdin.trim()) {
      const outputFile = join(TMP_DIR, `${Date.now()}-messages-${hookType.toLowerCase()}.json`);
      const stdinContent = {
        args,
        hookType,
        content: JSON.parse(stdin),
        timestamp: new Date().toISOString()
      };

    //   {
    //     "args": [],
    //     "hookType": "unknown",
    //     "content": {
    //       "session_id": "76815927-bb78-4de1-9544-a83a0f1dc363",
    //       "transcript_path": "/Users/rylandgoldstein/.claude/projects/-Users-rylandgoldstein-repos-claude-system/76815927-bb78-4de1-9544-a83a0f1dc363.jsonl",
    //       "cwd": "/Users/rylandgoldstein/repos/claude-system",
    //       "hook_event_name": "PreCompact",
    //       "trigger": "manual",
    //       "custom_instructions": ""
    //     },
    //     "timestamp": "2025-08-12T19:51:13.072Z"
    //   }

      const { transcript_path } = stdinContent.content;
      const transcript = readFileSync(transcript_path, 'utf8');

      // Extract only user and assistant messages
      const messages = extractMessages(transcript);

      // Generate structured summary with the complex schema (now works with v1.2.4!)
      console.log('ðŸš€ Generating structured summary with complex schema including enums...');
      const structuredSummary = await generateStructuredSummary(messages);

      // Write the filtered messages to a file
      const output = {
        session_id: stdinContent.content.session_id,
        hook_event_name: stdinContent.content.hook_event_name,
        trigger: stdinContent.content.trigger,
        timestamp: stdinContent.timestamp,
        message_count: messages.length,
        summary: structuredSummary || { error: 'Summary generation failed' },
        messages: messages
      };

      writeFileSync(outputFile, JSON.stringify(output, null, 2), 'utf8');
      console.log(`Extracted ${messages.length} messages to: ${outputFile}`);

      // Display the structured summary if generated
      if (structuredSummary) {
        console.log('\n=== ðŸŽ‰ STRUCTURED SUMMARY (with enum support!) ===');
        console.log('Brief:', structuredSummary.briefSummary);
        console.log('\nMain Topics:', structuredSummary.mainTopics.join(', '));
        console.log('\nUser Questions:');
        structuredSummary.userQuestions.forEach(q => {
          console.log(`  - ${q.question} (${q.answered ? 'answered' : 'not answered'})`);
          if (q.answerSummary) {
            console.log(`    Summary: ${q.answerSummary}`);
          }
        });
        console.log('\nKey Information:');
        structuredSummary.keyInformation.forEach(info => {
          console.log(`  - ${info.topic}: ${info.details}`);
        });
        if (structuredSummary.codeSnippets && structuredSummary.codeSnippets.length > 0) {
          console.log('\nCode Discussed:');
          structuredSummary.codeSnippets.forEach(code => {
            console.log(`  - ${code.language}: ${code.purpose} (mentioned: ${code.mentioned})`);
          });
        }
        if (structuredSummary.actionItems && structuredSummary.actionItems.length > 0) {
          console.log('\nAction Items:');
          structuredSummary.actionItems.forEach(item => {
            console.log(`  - ${item}`);
          });
        }
        console.log('\nðŸ“Š Resolution Status:', structuredSummary.resolution, '(enum value!)');
        console.log('ðŸ·ï¸ Tags:', structuredSummary.tags.join(', '));
        console.log('==================================================\n');

        // Write the summary to a separate file
        const summaryFile = join(TMP_DIR, `${Date.now()}-summary-${hookType.toLowerCase()}.json`);
        writeFileSync(summaryFile, JSON.stringify(structuredSummary, null, 2), 'utf8');
        console.log(`âœ… Structured summary written to: ${summaryFile}`);
      }

      // Also write messages in JSONL format for easy processing
      const jsonlFile = join(TMP_DIR, `${Date.now()}-messages-${hookType.toLowerCase()}.jsonl`);
      const jsonlContent = messages.map(msg => JSON.stringify(msg)).join('\n');
      writeFileSync(jsonlFile, jsonlContent, 'utf8');
      console.log(`JSONL messages written to: ${jsonlFile}`);
    }

    process.exit(0);
  } catch (error) {
    console.error('Error logging hook payload:', error);
    process.exit(1);
  }
}

main();

