import React from 'react';
import { render, RenderOptions } from '@testing-library/react';
import userEvent from '@testing-library/user-event';
import type { 
  AnyQuestion, 
  QuestionGroup, 
  ShortAnswerQuestion,
  LongFormQuestion,
  MultipleChoiceQuestion,
  TrueFalseQuestion,
  SliderQuestion,
  StackRankingQuestion,
  NumericQuestion,
  QuestionResponse 
} from '../types';

// Custom render function with providers
export function renderWithProviders(
  ui: React.ReactElement,
  options?: Omit<RenderOptions, 'wrapper'>
) {
  return render(ui, options);
}

// User event setup
export const user = userEvent.setup();

// Mock question factories
export const createMockShortAnswerQuestion = (overrides?: Partial<ShortAnswerQuestion>): ShortAnswerQuestion => ({
  id: 'short-1',
  type: 'short-answer',
  text: 'What is your name?',
  required: false,
  priority: 'medium',
  tags: ['personal'],
  placeholder: 'Enter your name',
  maxLength: 100,
  ...overrides
});

export const createMockLongFormQuestion = (overrides?: Partial<LongFormQuestion>): LongFormQuestion => ({
  id: 'long-1',
  type: 'long-form',
  text: 'Describe yourself',
  required: false,
  priority: 'medium',
  tags: ['personal'],
  rows: 5,
  maxLength: 500,
  enableMarkdown: false,
  ...overrides
});

export const createMockMultipleChoiceQuestion = (overrides?: Partial<MultipleChoiceQuestion>): MultipleChoiceQuestion => ({
  id: 'mc-1',
  type: 'multiple-choice',
  text: 'Select your skills',
  required: false,
  priority: 'medium',
  tags: ['skills'],
  options: [
    { id: 'js', label: 'JavaScript' },
    { id: 'ts', label: 'TypeScript' },
    { id: 'python', label: 'Python' }
  ],
  multiple: false,
  showOther: false,
  ...overrides
});

export const createMockTrueFalseQuestion = (overrides?: Partial<TrueFalseQuestion>): TrueFalseQuestion => ({
  id: 'tf-1',
  type: 'true-false',
  text: 'Do you agree?',
  required: false,
  priority: 'medium',
  tags: ['agreement'],
  ...overrides
});

export const createMockSliderQuestion = (overrides?: Partial<SliderQuestion>): SliderQuestion => ({
  id: 'slider-1',
  type: 'slider',
  text: 'Rate your experience',
  required: false,
  priority: 'medium',
  tags: ['rating'],
  min: 0,
  max: 10,
  step: 1,
  dual: false,
  showValue: true,
  ...overrides
});

export const createMockStackRankingQuestion = (overrides?: Partial<StackRankingQuestion>): StackRankingQuestion => ({
  id: 'stack-1',
  type: 'stack-ranking',
  text: 'Rank your priorities',
  required: false,
  priority: 'medium',
  tags: ['priorities'],
  items: [
    { id: 'item1', label: 'Item 1' },
    { id: 'item2', label: 'Item 2' },
    { id: 'item3', label: 'Item 3' }
  ],
  allowTies: false,
  ...overrides
});

export const createMockNumericQuestion = (overrides?: Partial<NumericQuestion>): NumericQuestion => ({
  id: 'numeric-1',
  type: 'numeric',
  text: 'Enter a number',
  required: false,
  priority: 'medium',
  tags: ['numeric'],
  min: 0,
  max: 100,
  step: 1,
  ...overrides
});

export const createMockQuestionGroup = (questions: AnyQuestion[], overrides?: Partial<QuestionGroup>): QuestionGroup => ({
  id: 'group-1',
  name: 'Test Group',
  description: 'A test question group',
  questions,
  priority: 'medium',
  tags: ['test'],
  collapsible: true,
  defaultExpanded: true,
  ...overrides
});

export function createMockResponse<T = unknown>(questionId: string, value: T): QuestionResponse<T> {
  return {
    questionId,
    value,
    timestamp: new Date(),
    valid: true,
    errors: []
  };
}

// Helper to wait for async updates
export const waitFor = (callback: () => boolean | void, timeout = 1000): Promise<void> => {
  return new Promise((resolve, reject) => {
    const startTime = Date.now();
    const interval = setInterval(() => {
      if (callback()) {
        clearInterval(interval);
        resolve();
      }
      if (Date.now() - startTime > timeout) {
        clearInterval(interval);
        reject(new Error('Timeout waiting for condition'));
      }
    }, 50);
  });
};

// Export everything from testing library
export * from '@testing-library/react';

