import React, { useState, useEffect } from 'react';
import {
  QuestionGroup,
  ConditionalLogicEngine
} from '../../ui/components/questions';
import type {
  AnyQuestion,
  QuestionGroup as QuestionGroupType,
  QuestionResponse,
  FormState
} from '../../ui/components/questions';

const QuestionsDemo: React.FC = () => {
  const [formState, setFormState] = useState<FormState>({
    responses: {},
    currentGroup: undefined,
    completedGroups: [],
    errors: {},
    isDirty: false,
    isSubmitting: false,
    isValid: false
  });

  const [conditionalEngine, setConditionalEngine] = useState<ConditionalLogicEngine | null>(null);
  const [visibleQuestions, setVisibleQuestions] = useState<Set<string>>(new Set());

  // Sample questions for demonstration
  const sampleQuestions: AnyQuestion[] = [
    {
      id: 'name',
      type: 'short-answer',
      text: 'What is your name?',
      description: 'Please enter your full name',
      placeholder: 'John Doe',
      required: true,
      priority: 'high',
      tags: ['personal'],
      maxLength: 100,
      suggestions: ['Alice Smith', 'Bob Johnson', 'Carol Williams']
    },
    {
      id: 'bio',
      type: 'long-form',
      text: 'Tell us about yourself',
      description: 'Share your background and interests',
      placeholder: 'I am a...',
      required: false,
      priority: 'medium',
      tags: ['personal'],
      maxLength: 500,
      rows: 5,
      enableMarkdown: true
    },
    {
      id: 'experience',
      type: 'multiple-choice',
      text: 'What is your experience level?',
      required: true,
      priority: 'high',
      tags: ['professional'],
      options: [
        { id: 'beginner', label: 'Beginner', description: '0-2 years' },
        { id: 'intermediate', label: 'Intermediate', description: '2-5 years' },
        { id: 'advanced', label: 'Advanced', description: '5-10 years' },
        { id: 'expert', label: 'Expert', description: '10+ years' }
      ],
      multiple: false,
      showOther: true,
      otherLabel: 'Other experience level'
    },
    {
      id: 'skills',
      type: 'multiple-choice',
      text: 'Select your skills',
      description: 'Choose all that apply',
      required: false,
      priority: 'medium',
      tags: ['professional'],
      options: [
        { id: 'javascript', label: 'JavaScript' },
        { id: 'typescript', label: 'TypeScript' },
        { id: 'react', label: 'React' },
        { id: 'nodejs', label: 'Node.js' },
        { id: 'python', label: 'Python' }
      ],
      multiple: true,
      showOther: false
    },
    {
      id: 'remote',
      type: 'true-false',
      text: 'Are you interested in remote work?',
      required: true,
      priority: 'medium',
      tags: ['preferences'],
      trueLabel: 'Yes, I prefer remote',
      falseLabel: 'No, I prefer in-office'
    },
    {
      id: 'remote-experience',
      type: 'slider',
      text: 'How many years of remote work experience do you have?',
      required: false,
      priority: 'low',
      tags: ['preferences'],
      min: 0,
      max: 20,
      step: 0.5,
      dual: false,
      showValue: true,
      unit: ' years',
      marks: [
        { value: 0, label: 'None' },
        { value: 5, label: '5' },
        { value: 10, label: '10' },
        { value: 15, label: '15' },
        { value: 20, label: '20+' }
      ],
      conditions: [
        {
          questionId: 'remote',
          operator: 'equals',
          value: true,
          action: 'show'
        }
      ]
    },
    {
      id: 'salary-range',
      type: 'slider',
      text: 'What is your expected salary range?',
      required: true,
      priority: 'high',
      tags: ['compensation'],
      min: 30000,
      max: 200000,
      step: 5000,
      dual: true,
      showValue: true,
      unit: '$',
      marks: [
        { value: 30000, label: '$30k' },
        { value: 100000, label: '$100k' },
        { value: 200000, label: '$200k' }
      ]
    },
    {
      id: 'priorities',
      type: 'stack-ranking',
      text: 'Rank your priorities',
      description: 'Drag to reorder from most to least important',
      required: true,
      priority: 'medium',
      tags: ['preferences'],
      items: [
        { id: 'salary', label: 'Competitive Salary', description: 'Above market compensation' },
        { id: 'balance', label: 'Work-Life Balance', description: 'Flexible hours and time off' },
        { id: 'growth', label: 'Career Growth', description: 'Learning and advancement opportunities' },
        { id: 'culture', label: 'Company Culture', description: 'Positive work environment' },
        { id: 'tech', label: 'Technology Stack', description: 'Modern tools and frameworks', fixed: false }
      ],
      allowTies: false
    },
    {
      id: 'team-size',
      type: 'numeric',
      text: 'What is your ideal team size?',
      required: false,
      priority: 'low',
      tags: ['preferences'],
      min: 1,
      max: 100,
      step: 1,
      precision: 0,
      unit: ' people'
    },
    {
      id: 'availability',
      type: 'numeric',
      text: 'How many hours per week are you available?',
      required: true,
      priority: 'high',
      tags: ['availability'],
      min: 0,
      max: 168,
      step: 0.5,
      precision: 1,
      unit: ' hours',
      showAsPercentage: true
    },
    {
      id: 'start-date',
      type: 'short-answer',
      text: 'When can you start?',
      placeholder: 'MM/DD/YYYY or "Immediately"',
      required: true,
      priority: 'critical',
      tags: ['availability'],
      pattern: '^(\\d{2}/\\d{2}/\\d{4}|Immediately)$'
    }
  ];

  const questionGroups: QuestionGroupType[] = [
    {
      id: 'personal',
      name: 'Personal Information',
      description: 'Basic information about you',
      questions: sampleQuestions.filter(q => q.tags.includes('personal')),
      priority: 'high',
      tags: ['required'],
      collapsible: true,
      defaultExpanded: true
    },
    {
      id: 'professional',
      name: 'Professional Background',
      description: 'Your experience and skills',
      questions: sampleQuestions.filter(q => q.tags.includes('professional')),
      priority: 'high',
      tags: ['required'],
      collapsible: true,
      defaultExpanded: false
    },
    {
      id: 'preferences',
      name: 'Work Preferences',
      description: 'What you are looking for',
      questions: sampleQuestions.filter(q => q.tags.includes('preferences')),
      priority: 'medium',
      tags: ['optional'],
      collapsible: true,
      defaultExpanded: false
    },
    {
      id: 'other',
      name: 'Additional Information',
      description: 'Compensation and availability',
      questions: sampleQuestions.filter(q => 
        q.tags.includes('compensation') || q.tags.includes('availability')
      ),
      priority: 'low',
      tags: ['optional'],
      collapsible: true,
      defaultExpanded: false
    }
  ];

  useEffect(() => {
    // Initialize conditional logic engine
    const engine = new ConditionalLogicEngine(sampleQuestions, {});
    setConditionalEngine(engine);
    setVisibleQuestions(engine.getVisibleQuestions());
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  const handleQuestionChange = (questionId: string, value: unknown) => {
    const response: QuestionResponse = {
      questionId,
      value,
      timestamp: new Date(),
      valid: true, // Simple validation for demo
      errors: []
    };

    const newResponses = {
      ...formState.responses,
      [questionId]: response
    };

    setFormState(prev => ({
      ...prev,
      responses: newResponses,
      isDirty: true
    }));

    // Update conditional logic
    if (conditionalEngine) {
      conditionalEngine.updateResponse(questionId, response);
      setVisibleQuestions(conditionalEngine.getVisibleQuestions());
    }
  };

  const handleGroupComplete = (groupId: string) => {
    setFormState(prev => ({
      ...prev,
      completedGroups: [...prev.completedGroups.filter(id => id !== groupId), groupId]
    }));
  };

  const handleSubmit = () => {
    setFormState(prev => ({ ...prev, isSubmitting: true }));

    // Simulate submission
    setTimeout(() => {
      console.log('Form submitted:', formState.responses);
      alert('Form submitted successfully! Check console for data.');
      setFormState(prev => ({ ...prev, isSubmitting: false, isDirty: false }));
    }, 1000);
  };

  const handleReset = () => {
    setFormState({
      responses: {},
      currentGroup: undefined,
      completedGroups: [],
      errors: {},
      isDirty: false,
      isSubmitting: false,
      isValid: false
    });

    if (conditionalEngine) {
      const engine = new ConditionalLogicEngine(sampleQuestions, {});
      setConditionalEngine(engine);
      setVisibleQuestions(engine.getVisibleQuestions());
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-4xl mx-auto px-4">
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            Question Components Demo
          </h1>
          <p className="text-gray-600">
            Interactive demonstration of all question types with conditional logic
          </p>
        </div>

        <div className="mb-6">
          <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
            <h2 className="font-semibold text-blue-900 mb-2">Demo Features:</h2>
            <ul className="text-sm text-blue-800 space-y-1">
              <li>â€¢ All 7 question types (Short Answer, Long Form, Multiple Choice, True/False, Slider, Stack Ranking, Numeric)</li>
              <li>â€¢ Conditional questions (answer &quot;Yes&quot; to remote work to see follow-up)</li>
              <li>â€¢ Priority indicators (critical, high, medium, low)</li>
              <li>â€¢ Question groups with progress tracking</li>
              <li>â€¢ Validation and error states</li>
              <li>â€¢ Collapsible sections</li>
              <li>â€¢ Tag system for categorization</li>
            </ul>
          </div>
        </div>

        <div className="space-y-6">
          {questionGroups.map(group => (
            <QuestionGroup
              key={group.id}
              disabled={formState.isSubmitting}
              group={{
                ...group,
                questions: group.questions.filter((q: AnyQuestion) => visibleQuestions.has(q.id))
              }}
              responses={formState.responses}
              onGroupComplete={handleGroupComplete}
              onQuestionChange={handleQuestionChange}
            />
          ))}
        </div>

        <div className="mt-8 flex gap-4 justify-center">
          <button
            className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed font-medium"
            disabled={formState.isSubmitting}
            onClick={handleSubmit}
          >
            {formState.isSubmitting ? 'Submitting...' : 'Submit Form'}
          </button>
          <button
            className="px-6 py-3 bg-gray-200 text-gray-700 rounded-lg hover:bg-gray-300 disabled:opacity-50 disabled:cursor-not-allowed font-medium"
            disabled={formState.isSubmitting}
            onClick={handleReset}
          >
            Reset Form
          </button>
        </div>

        {formState.isDirty && (
          <div className="mt-4 text-center text-sm text-gray-600">
            Form has unsaved changes
          </div>
        )}

        <div className="mt-8 bg-gray-100 rounded-lg p-6">
          <h2 className="text-lg font-semibold mb-4">Form State (Debug)</h2>
          <pre className="text-xs bg-white p-4 rounded overflow-auto max-h-60">
            {JSON.stringify(formState.responses, null, 2)}
          </pre>
        </div>
      </div>
    </div>
  );
};

export default QuestionsDemo;

