import React from 'react';
import { render, RenderOptions } from '@testing-library/react';
import type { 
  AnyQuestion, 
  QuestionGroup, 
  ShortAnswerQuestion,
  LongFormQuestion,
  MultipleChoiceQuestion,
  TrueFalseQuestion,
  SliderQuestion,
  StackRankingQuestion,
  NumericQuestion,
  QuestionResponse 
} from '../types';

// Custom render function with providers
export function renderWithProviders(
  ui: React.ReactElement,
  options?: Omit<RenderOptions, 'wrapper'>
) {
  return render(ui, options);
}

// User event setup - lazy load to avoid initialization issues
export const user = {
  click: async (element: Element) => {
    const event = new MouseEvent('click', { bubbles: true });
    element.dispatchEvent(event);
  },
  type: async (element: Element, text: string) => {
    if (element instanceof HTMLInputElement || element instanceof HTMLTextAreaElement) {
      element.value = text;
      element.dispatchEvent(new Event('input', { bubbles: true }));
      element.dispatchEvent(new Event('change', { bubbles: true }));
    }
  },
  clear: async (element: Element) => {
    if (element instanceof HTMLInputElement || element instanceof HTMLTextAreaElement) {
      element.value = '';
      element.dispatchEvent(new Event('input', { bubbles: true }));
      element.dispatchEvent(new Event('change', { bubbles: true }));
    }
  }
};

// Mock question factories
export const createMockShortAnswerQuestion = (overrides?: Partial<ShortAnswerQuestion>): ShortAnswerQuestion => ({
  id: 'short-1',
  type: 'short-answer',
  text: 'What is your name?',
  required: false,
  priority: 'medium',
  tags: ['personal'],
  placeholder: 'Enter your name',
  maxLength: 100,
  ...overrides
});

export const createMockLongFormQuestion = (overrides?: Partial<LongFormQuestion>): LongFormQuestion => ({
