/**
 * Test utilities for DOM-based tests using Bun and Happy DOM
 */
import React from 'react';
import { render, type RenderOptions } from '@testing-library/react';
import { mock } from 'bun:test';
import type { 
  AnyQuestion, 
  QuestionGroup, 
  ShortAnswerQuestion,
  LongFormQuestion,
  MultipleChoiceQuestion,
  TrueFalseQuestion,
  SliderQuestion,
  StackRankingQuestion,
  NumericQuestion,
  QuestionResponse 
} from '../types';

// Re-export testing library utilities
export { screen, fireEvent, waitFor, within } from '@testing-library/react';

// Custom render function with providers
export function renderWithProviders(
  ui: React.ReactElement,
  options?: Omit<RenderOptions, 'wrapper'>
) {
  return render(ui, options);
}

// Mock function creator
export const createMock = () => mock(() => {});

// User event simulation helpers
export const user = {
  click: async (element: Element) => {
    const event = new MouseEvent('click', { bubbles: true });
    element.dispatchEvent(event);
  },
  type: async (element: Element, text: string) => {
    if (element instanceof HTMLInputElement || element instanceof HTMLTextAreaElement) {
      element.value = text;
      element.dispatchEvent(new Event('input', { bubbles: true }));
      element.dispatchEvent(new Event('change', { bubbles: true }));
    }
  },
  clear: async (element: Element) => {
    if (element instanceof HTMLInputElement || element instanceof HTMLTextAreaElement) {
      element.value = '';
      element.dispatchEvent(new Event('input', { bubbles: true }));
      element.dispatchEvent(new Event('change', { bubbles: true }));
