#!/usr/bin/env bun

/**
 * Test runner script that properly sets up the DOM environment for tests
 */

// Import Happy DOM setup first
import './src/ui/components/questions/__tests__/setup.ts';

// Run tests
import { spawn } from 'child_process';

const args = process.argv.slice(2);
const testPath = args[0] || 'src/ui/components/questions/__tests__';

console.log('Running tests with DOM environment...');
console.log(`Test path: ${testPath}`);

const proc = spawn('bun', ['test', testPath], {
  stdio: 'inherit',
  env: {
    ...process.env,
    NODE_ENV: 'test'
  }
});

proc.on('exit', (code) => {
  process.exit(code || 0);
});

